import { useState, useEffect } from 'react';
import ConnectRow from '../ConnectRow/ConnectRow';
import Grid2 from '@mui/material/Unstable_Grid2/Grid2';

export default function ConnectBoard() {
  const [board, setBoard] = useState([]);

  // Set up the board at the beginning of the game
  useEffect(() => {
    const newBoard = [];
    for (let i = 0; i < 6; i++) {
      newBoard.push([]);
      for (let j = 0; j < 7; j++) {
        newBoard[i].push(0);
      }
    }
    setBoard(newBoard);
  }, []);

  function insertPieceAtCol(col, player) {
    // Check if the column has an open space
    if (board[col].includes(0)) {
      for (let i = 0; i < 6; i++) {
        if (board[i][col] === 0) {
          continue;
        }
        else if (board[i][col] !== 0) {
          board[i - 1][col] = player;
          break;
        }
      }
    }
    else {
      // If the column is full, do nothing
      return;
    }
  }

  // visualize a connect 4 board
  return (
    <Grid2 container spacing={0}
    direction="row"
    >
      {board.map((row, i) => (
        <Grid2 item xs={12}>
          <ConnectRow row={row} />
        </Grid2>
      ))}
      <Grid2 item xs={12}>
        <button onClick={() => insertPieceAtCol(0, 1)}>Insert Piece</button>
        <button onClick={() => insertPieceAtCol(1, 1)}>Insert Piece</button>
        <button onClick={() => insertPieceAtCol(2, 1)}>Insert Piece</button>
        <button onClick={() => insertPieceAtCol(3, 1)}>Insert Piece</button>
        <button onClick={() => insertPieceAtCol(4, 1)}>Insert Piece</button>
        <button onClick={() => insertPieceAtCol(5, 1)}>Insert Piece</button>
        <button onClick={() => insertPieceAtCol(6, 1)}>Insert Piece</button>
        <button onClick={() => insertPieceAtCol(7, 1)}>Insert Piece</button>
      </Grid2>
    </Grid2>
  );

}
