import { useState, useEffect } from 'react';
import Grid2 from '@mui/material/Unstable_Grid2/Grid2';
import empty from '../../assets/BoardImages/empty.png';
import yellow from '../../assets/BoardImages/yellow.png';
import red from '../../assets/BoardImages/red.png';

export default function ConnectBoard() {
  const [board, setBoard] = useState([]);
  const [player, setPlayer] = useState(1);
  const colNums = [0, 1, 2, 3, 4, 5, 6];

  // Set up the board at the beginning of the game
  useEffect(() => {
    const newBoard = [];
    for (let i = 0; i < 6; i++) {
      newBoard.push([]);
      for (let j = 0; j < 7; j++) {
        newBoard[i].push(0);
      }
    }
    setBoard(newBoard);
  }, []);

  function insertPieceAtCol(col, player) {
    var newBoard = [...board];
    // Check if the column has an open space
    if (newBoard[0][col] === 0) {
      for (let i = 0; i < 6; i++) {
        if (newBoard[i][col] !== 0) {
          newBoard[i - 1][col] = player;
          break;
        }
        else if (i === 5) {
          newBoard[i][col] = player;
          break;
        }
      }
    }
    else {
      // If the column is full, do nothing
      return;
    }
    console.log("board: ", newBoard)
    setBoard(newBoard);
    setPlayer(-player);
  }

  // visualize a connect 4 board
  return (
    <>
      <Grid2 container spacing={0}
      direction="row"
      justifyContent={"center"}
      >
        {board.map((row, i) => (
          <Grid2 item xs={12}>
            <Grid2 container spacing={0}
              direction={"row"}
            >
                {row.map((col, i) => (
                  <Grid2 item xs={1.7}
                  onClick
                  >
                    {
                      (col === 0) ? <img src={empty} alt="empty piece"></img> : (col === -1) ? <img src={yellow} alt="yellow piece"></img> : <img src={red} alt="red piece"></img>
                    }
                  </Grid2>
                ))}
              </Grid2>
          </Grid2>
        ))}

      </Grid2>

      <Grid2 container spacing={13}
        direction={"row"}
        justifyContent="center"
      >
        {colNums.map((x) => (
          <Grid2 item xs={1}>
            <button onClick={() => insertPieceAtCol(x, player)}>Insert Piece</button>
          </Grid2>
        ))}
      </Grid2>
    </>
  );

}
