import { useState, useEffect } from 'react';

export default function ConnectBoard() {
  const [board, setBoard] = useState([]);

  // Set up the board at the beginning of the game
  useEffect(() => {
    const newBoard = [];
    for (let i = 0; i < 7; i++) {
      newBoard.push([]);
      for (let j = 0; j < 6; j++) {
        newBoard[i].push(0);
      }
    }
    setBoard(newBoard);
  }, []);

  function insertPieceAtCol(col, player) {
    // Check if the column has an open space
    if (board[col].includes(0)) {
      for (let i = 0; i < 6; i++) {
        if (board[i][col] === 0) {
          continue;
        }
        else if (board[i][col] !== 0) {
          board[i - 1][col] = player;
          break;
        }
      }
    }
    else {
      // If the column is full, do nothing
      return;
    }
  }

  // visualize a connect 4 board
  return (
    <div className="connect-board">
      {board.map((row, i) => (
        <div className="connect-row" key={i}>
          {row.map((col, j) => (
            <div className="connect-col" key={j}>
              {col}
            </div>
          ))}
        </div>
      ))}
    </div>
  );

}
