import { useState, useEffect } from 'react';
import Grid2 from '@mui/material/Unstable_Grid2/Grid2';
import empty from '../../assets/BoardImages/empty.png';
import yellow from '../../assets/BoardImages/yellow.png';
import red from '../../assets/BoardImages/red.png';

export default function ConnectBoard() {
  const [board, setBoard] = useState([]);

  // Set up the board at the beginning of the game
  useEffect(() => {
    const newBoard = [];
    for (let i = 0; i < 6; i++) {
      newBoard.push([]);
      for (let j = 0; j < 7; j++) {
        newBoard[i].push(0);
      }
    }
    setBoard(newBoard);
  }, []);

  function insertPieceAtCol(col, player) {
    var newBoard = board;
    newBoard[0][col] = player;
    setBoard(newBoard);
    // var newBoard = board;
    // // Check if the column has an open space
    // if (newBoard[col].includes(0)) {
    //   for (let i = 0; i < 6; i++) {
    //     if (newBoard[i][col] === 0) {
    //       continue;
    //     }
    //     else if (newBoard[i][col] !== 0) {
    //       newBoard[i - 1][col] = player;
    //       break;
    //     }
    //   }
    // }
    // else {
    //   // If the column is full, do nothing
    //   return;
    // }
    // setBoard(newBoard);
  }

  // visualize a connect 4 board
  return (
    <Grid2 container spacing={0}
    direction="row"
    >
      {board.map((row, i) => (
        <Grid2 item xs={12}>
          <Grid2 container spacing={0}
            direction={"row"}
          >
            <Grid2 container spacing={0}>
              {row.map((col, i) => (
                <Grid2 item xs={1.7}>
                  {
                    (col === 0) ? <img src={empty} alt="empty piece"></img> : (col === -1) ? <img src={yellow} alt="yellow piece"></img> : <img src={red} alt="red piece"></img>
                  }
                </Grid2>
              ))}
            </Grid2>
          </Grid2>
        </Grid2>
      ))}
      <Grid2 item xs={12}>
        <button onClick={() => insertPieceAtCol(0, 1)}>Insert Piece</button>
        <button onClick={() => insertPieceAtCol(1, 1)}>Insert Piece</button>
        <button onClick={() => insertPieceAtCol(2, 1)}>Insert Piece</button>
        <button onClick={() => insertPieceAtCol(3, 1)}>Insert Piece</button>
        <button onClick={() => insertPieceAtCol(4, 1)}>Insert Piece</button>
        <button onClick={() => insertPieceAtCol(5, 1)}>Insert Piece</button>
        <button onClick={() => insertPieceAtCol(6, 1)}>Insert Piece</button>
      </Grid2>
    </Grid2>
  );

}
